import React, { useState } from 'react';
import { quotesData } from './quotes';

const CloudFormation = ({ onChangeBackground }) => {
  const [clouds, setClouds] = useState([]);
  const [currentQuote, setCurrentQuote] = useState('');
  const [cloudCount, setCloudCount] = useState(0);

  const addCloud = () => {
    if (cloudCount < quotesData.length) {
      const randomIndex = Math.floor(Math.random() * quotesData.length);
      const randomQuote = quotesData[randomIndex].quote;

      const newCloud = (
        <div
          key={Date.now()}
          className="text-2xl mb-4 cursor-pointer"
          onClick={() => handleCloudClick(randomQuote)}
        >
          ‚òÅÔ∏è
        </div>
      );

      setClouds([...clouds, newCloud]);
      setCloudCount(cloudCount + 1);
      onChangeBackground();
    }
  };

  const handleCloudClick = (quote) => {
    setCurrentQuote(quote);
  };

  return (
    <div>
      <button
        className="bg-blue-500 text-white px-4 py-2 rounded-md mt-4"
        onClick={addCloud}
        disabled={cloudCount >= quotesData.length}
      >
        Add Cloud
      </button>
      <div className='overflow-x-auto max-w-screen-lg mx-auto p-4'>
        <div className="flex space-x-4 mb-4 mt-6">{clouds}</div>
      </div>
      {currentQuote && <p className="text-xl">{currentQuote}</p>}
    </div>
  );
};

export default CloudFormation;
