// src/components/CloudFormation.js
import React, { useState } from 'react';

const CloudFormation = ({ onChangeBackground }) => {
  const [clouds, setClouds] = useState([]);
  const [currentQuote, setCurrentQuote] = useState('');
  const [cloudCount, setCloudCount] = useState(0); // Counter for the number of clouds
  const quotes = [
    "You are the sunshine in my life.",
    "My love for you is endless.",
    "You complete me.",
    "You make my heart skip a beat.",
    "Forever yours.",

  ];

  const addCloud = () => {
    if (cloudCount < quotes) {
      const randomQuote = quotes[cloudCount];
      const newCloud = (
        <div
          key={Date.now()} // Use timestamp as a unique key to avoid rendering issues
          className="text-2xl mb-4 cursor-pointer"
          onClick={() => handleCloudClick(randomQuote)}
        >
          ‚òÅÔ∏è
        </div>
      );

      setClouds([...clouds, newCloud]);
      setCloudCount(cloudCount + 1); // Increment the cloud count
      onChangeBackground(); // Change the background color
    }
  };

  const handleCloudClick = (quote) => {
    setCurrentQuote(quote); // Update the current quote state
  };

  return (
    <div className="overflow-hidden">
      <button
        className="bg-blue-500 text-white px-4 py-2 rounded-md mt-4"
        onClick={addCloud}
        disabled={cloudCount >= quotes.length} // Disable the button when the maximum clouds are reached
      >
        Add Cloud
      </button>
      <div className="overflow-x-scroll">
        <div className="flex space-x-4 mb-4 mt-6">{clouds}</div>
        {currentQuote && <p className="text-xl">{currentQuote}</p>}
      </div>
    </div>
  );
};

export default CloudFormation;
