// src/components/CloudFormation.js
import React, { useState } from 'react';
import { quotesData } from './quotes';

const CloudFormation = ({ onChangeBackground }) => {
  const [clouds, setClouds] = useState([]);
  const [currentQuote, setCurrentQuote] = useState('');
  const [cloudCount, setCloudCount] = useState(0); // Counter for the number of clouds
  const quotes = quotesData;

  const addCloud = () => {
    if (cloudCount < quotes.length) {
      const randomQuote = quotes[cloudCount];

      const newCloud = (
        <div
          key={Date.now()}
          className="text-2xl mb-4 cursor-pointer"
          onClick={() => handleCloudClick(randomQuote)}
        >
          ‚òÅÔ∏è
        </div>
      );

      setClouds([...clouds, newCloud]);
      setCloudCount(cloudCount + 1);
      onChangeBackground();
    }
  };

  const handleCloudClick = (quote) => {
    setCurrentQuote(quote);
  };

  return (
    <div>
      <button
        className="bg-blue-500 text-white px-4 py-2 rounded-md mt-4"
        onClick={addCloud}
        disabled={cloudCount >= quotes.length}
      >
        Add Cloud
      </button>
      <div className='overflow-x-auto max-w-screen-lg mx-auto p-4'>
        <div className="flex flex-col space-y-4 mb-4 mt-6">
          {clouds.map((cloud, index) => (
            <div key={index} className="flex items-center space-x-2">
              <span className="text-xl">{index + 1}.</span>
              {cloud}
            </div>
          ))}
        </div>
      </div>
      {currentQuote && <p className="text-xl">{currentQuote}</p>}
    </div>
  );
};

export default CloudFormation;
